mapper.select:
select record.*, doctor_score from record left join record_doctor on record.id = record_doctor.record_id
doctor_id = #{doctorId}
and record.history_num like concat('%', #{historyNum}, '%')
and record.name like concat('%', #{patientName}, '%')
and record.place like concat('%', #{place}, '%')
and record.date between #{beginTime} and #{endTime}

select record.* from record left join record_doctor on record.id = record_doctor.record_id
doctor_id = #{doctorId}
and record.history_num like concat('%', #{historyNum}, '%')
and record.name like concat('%', #{patientName}, '%')
and record.place like concat('%', #{place}, '%')
and record.date between #{beginTime} and #{endTime}

select SUM(doctor_score) from record left join record_doctor on record.id = record_doctor.record_id
doctor_id = #{doctorId}
and record.history_num like concat('%', #{historyNum}, '%')
and record.name like concat('%', #{patientName}, '%')
and record.place like concat('%', #{place}, '%')
and record.date between #{beginTime} and #{endTime}
